# In[ ]:


def outliers_cutter(df, phases, concs, koef=1.5):
    """
    –î–∞–Ω–Ω–∞—è —Ñ—É–Ω–∫—Ü–∏—è –¥–ª—è –∫–∞–∂–¥–æ–≥–æ –∑–Ω–∞—á–µ–Ω–∏—è –∫–æ–Ω—Ü–µ–Ω—Ç—Ä–∞—Ü–∏–∏ –Ω–∞ –∫–∞–∂–¥–æ–π –∏–∑ —Å—Ç–∞–¥–∏–π –¥–µ–ª–∞–µ—Ç —Å—Ä–µ–∑ –ø–æ –¥–∏–∞–ø–∞–∑–æ–Ω—É Q1-1.5*IQR - Q3+1.5*IQR
    """
    df = df.copy()
    for phase in phases:
        for conc in concs:

            Q1 = df[phase + conc].quantile(0.25)
            Q3 = df[phase + conc].quantile(0.75)
            IQR = Q3 - Q1
            df = df[(df[phase + conc] > Q1-koef*IQR ) & (df[phase + conc] < Q3+koef*IQR)]

    return df


